import {
  Component,
  OnInit,
  Input,
  Output,
  EventEmitter,
  Inject,
  ViewChild,
} from '@angular/core';
import { MAT_LEGACY_DIALOG_DATA as MAT_DIALOG_DATA, MatLegacyDialogRef as MatDialogRef } from '@angular/material/legacy-dialog';
import { MatLegacySelectionList as MatSelectionList } from '@angular/material/legacy-list';

@Component({
  selector: 'app-options-dialog',
  templateUrl: './options-dialog.component.html',
  styleUrls: ['./options-dialog.component.scss'],
})
export class OptionsDialogComponent<T> implements OnInit {
  @ViewChild('options', { static: true }) options: MatSelectionList;

  constructor(
    public dialogRef: MatDialogRef<OptionsDialogComponent<T>>,
    @Inject(MAT_DIALOG_DATA) public data: any
  ) {}

  ngOnInit(): void {}

  closeDialog() {
    this.dialogRef.close({
      data: this.options.selectedOptions.selected[0].value,
    });
  }

  test() {
    console.log('test');
  }
}
<div mat-dialog-title fxLayout="row" fxLayoutAlign="space-between center">
  <div fxFlex="90">
    {{ data.title | translate }}
  </div>
  <div fxFlex="10">
    <button
      type="button"
      mat-icon-button
      matSuffix
      mat-dialog-close
      aria-label="close"
    >
      <mat-icon>close</mat-icon>
    </button>
  </div>
</div>

<div mat-dialog-content>
  <mat-selection-list
    #options
    [multiple]="false"
    (selectionChange)="closeDialog()"
  >
    <mat-list-option
      *ngFor="let option of data.options; let isFirst = first"
      [value]="option"
      cdkFocusInitial
    >
      <p *ngIf="data.labelField; else plainOption">
        {{ option[data.labelField] }}
      </p>
      <ng-template #plainOption>
        {{ option }}
      </ng-template>
    </mat-list-option>

