import { Component, Inject, OnInit } from '@angular/core';
import { MatLegacyDialogRef as MatDialogRef, MAT_LEGACY_DIALOG_DATA as MAT_DIALOG_DATA } from '@angular/material/legacy-dialog';

export interface ConfirmationData {
  confirmationTitle: string;
  confirmationMessage: string;
  confirmationCancelButton: string;
  confirmationConfirmButton: string;
}

@Component({
  selector: 'app-confirm',
  templateUrl: './confirm-dialog.component.html',
  styleUrls: ['./confirm-dialog.component.scss']
})
/**
 * Operate in a popup
 * 1. ask for confirmation
 * 2. send a response back to the calling component with the answer
 */
export class ConfirmDialogComponent implements OnInit {
  constructor(
    public dialogRef: MatDialogRef<ConfirmDialogComponent>,
    @Inject(MAT_DIALOG_DATA) public data: ConfirmationData
  ) {}

  ngOnInit() {}

  onNoClick(): void {
    this.dialogRef.close();
  }

  onYesClick(): void {
    this.dialogRef.close({ isConfirmed: true });
  }
}
<h1 mat-dialog-title>
  {{ this.data.confirmationTitle }}
</h1>
<div mat-dialog-content>
  <p>{{ this.data.confirmationMessage }}</p>
</div>
<div mat-dialog-actions align="end">
  <button mat-flat-button (click)="onNoClick()" cdkFocusInitial>
    {{ this.data.confirmationCancelButton }}</button
  >&nbsp;
  <button mat-flat-button (click)="onYesClick()" color="warn">
    {{ this.data.confirmationConfirmButton }}
  </button>
</div>
