import {
  Component,
  OnInit,
  Input,
  HostListener,
  ElementRef,
} from '@angular/core';
import { AuthService } from '@src/app/modules/auth/auth.service';

import { User } from '@src/app/core/models/user/user.model';

@Component({
  selector: 'ma-user-menu',
  templateUrl: './user-menu.component.html',
  styleUrls: ['./user-menu.component.scss'],
})
export class UserMenuComponent implements OnInit {
  isOpen = false;

  @Input() currentUser: User = null;

  @HostListener('document:click', ['$event', '$event.target'])
  onClick(event: MouseEvent, targetElement: HTMLElement) {
    if (!targetElement) {
      return;
    }

    const clickedInside = this.elementRef.nativeElement.contains(targetElement);
    if (!clickedInside) {
      this.isOpen = false;
    }
  }

  constructor(
    private authService: AuthService,
    private elementRef: ElementRef
  ) {}

  ngOnInit() {
    this.currentUser = this.authService.getUser();
  }
}
<div class="toolbar-user-container">
  <button
    mat-button
    (click)="isOpen = !isOpen"
    class="toolbar-user-btn"
    [class.open]="isOpen"
  >
    <!-- [ngStyle.xs]="{ 'min-width': '70px' }" -->
    <span fxLayout="row" fxLayoutAlign="start center">
      <img class="avatar" src="assets/images/avatars/noavatar.png" />
      <!-- fxHide fxShow.gt-xs -->
      <span class="name">{{ currentUser?.email }}</span>
      <mat-icon class="icon">keyboard_arrow_down</mat-icon>
    </span>
  </button>

  <div class="dropdown mat-elevation-z1" [class.open]="isOpen">
    <div class="content">
      <mat-nav-list>
        <mat-list-item>
          <a matLine>Profile</a>
          <button mat-icon-button><mat-icon>account_circle</mat-icon></button>
        </mat-list-item>
        <mat-list-item>
          <a matLine>Settings</a>
          <button mat-icon-button><mat-icon>settings</mat-icon></button>
        </mat-list-item>
        <mat-list-item>
          <a matLine>Help</a>
          <button mat-icon-button><mat-icon>help</mat-icon></button>
        </mat-list-item>

        <mat-divider></mat-divider>

        <mat-list-item>
          <a matLine routerLink="/auth/logout">{{ "sign-out" | translate }}</a>
          <button mat-icon-button><mat-icon>exit_to_app</mat-icon></button>
        </mat-list-item>
      </mat-nav-list>
    </div>
  </div>
</div>
